    // }, 6000);
    // axios.get('http://yts-proxy.now.sh/list_movies.json');
    this.getMovies();

  }
  render() {
    const { isLoading } = this.state;
    return <div>{isLoading ? "Loading..." : "We are ready"}</div>;
  }
}

export default App;
